mapper.insert:
 	INSERT
	INTO 	users( 
					user_id 
				, 	user_name 
				, 	password 
				, 	role 
				, 	ssn
				, 	cell_phone
				, 	addr
				, 	email 
				, reg_date
	) VALUES(	
			#{userId} 
		, 	#{userName} 
		, 	#{password} 
		, 	'user' 
		, 	#{ssn:VARCHAR} 
		, 	#{phone:VARCHAR} 
		, 	#{addr:VARCHAR} 
		, 	#{email:VARCHAR} 
		, 	SYSDATE 
		)

mapper.update:
UPDATE	users
SET
		user_name = #{userName} 
	,	cell_phone = #{phone}
	,	addr = #{addr} 
	,	email =	#{email}
WHERE user_id = #{userId}

mapper.select:
  	SELECT *
  	FROM (	
  			SELECT 	inner_table.* 
  				, 	ROWNUM AS row_seq
  			FROM(	
  					SELECT user_id 
  						, 	user_name 
  						, 	email
					FROM users
					
						
							
				 				user_id = #{searchKeyword}
							
							
				 				user_name = #{searchKeyword}
							
						
					
					ORDER 
						BY 	user_id 
					) inner_table
			WHERE ROWNUM < = #{endRowNum} 
		)
	WHERE 	row_seq 
		BETWEEN #{startRowNum} 
			AND #{endRowNum}

  	SELECT 	COUNT(*)
  	FROM(	
  			SELECT 	user_id 
  				, 	user_name 
  				, 	email
			FROM 	users
			
				
					
			 			user_id = #{searchKeyword}
					
					
			 			user_name = #{searchKeyword}
					
				
			 ) countTable
